gallery.xscript:

(function () {
	var files = new Array();
	var attributes = attributemap("dat/crres-orbits.dat");
	for (i = 350; i < 500; i++) {
		files[i-350] = ["o" + sprintf('%04d',i) + ".gif",i,attributes[i][0],attributes[i][1]];
	}
	return files;						
})


(function () {
	files = new Array();
	var k = 0;
	for (i = 1911; i < 1976; i++) {
		files[k] = ["171A/CR" + sprintf('%4d',i) + "_171A_sinlat_a.gif",i];
		k = k+1;
	}
	for (i = 1976; i < 1990; i++) {
		files[k] = ["171A/CR" + sprintf('%4d',i) + "_171A_sinlat_b.gif",i];
		k = k+1;
	}
	for (i = 1990; i < 2056; i++) {
		files[k] = ["171A/CR" + sprintf('%4d',i) + "_171A_sinlat_c.gif",i];
		k = k+1;
	}
	return files;						
})


